import axios from 'axios';

export const BASE_URL = 'https://ypapi.formz.in/api/public/videos';

const options = {
 params: {
    method:'GET',
    maxResults: 50,
 },
};

export const fetchFromAPI = async () => {
 try {
    const { data } = await axios.get(BASE_URL, options);
    return data;
 } catch (error) {
    console.error('Error fetching data: ', error);
 }
};
