import axios from 'axios';

export const BASE_URL = 'https://ypapi.formz.in/api/public/videos';

const options = {
  params: {
    method:'GET',
    maxResults: 50,
  },
  headers: {
    'X-RapidAPI-Key': process.env.REACT_APP_RAPID_API_KEY,
    'X-RapidAPI-Host': 'https://ypapi.formz.in/api/public/videos',
  },
};

export const fetchFromAPI = async (url) => {
  const { data } = await axios.get(`${BASE_URL}/${url}`, options);

  return data;
};
